import React from "react";
import ClockTimezone from "../Clock";

export default class Clocks extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoading: false,
      items: [],
    };
  }
  fetchTimeZones() {
    fetch(`http://worldtimeapi.org/api/timezone`)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          items: data,
          isLoading: false,
        })
      }
      )
      // Catch any errors we hit and update the app
      .catch((error) => this.setState({ error, isLoading: false }));
  }
  componentDidMount() {
    this.fetchTimeZones();
  }
  render() {
    const { isLoading, items, error } = this.state;
    const newItems = items.filter(timezone => timezone.includes('/')).slice(1,5);
    return (
      <React.Fragment>
        <h2>Clocks</h2>
        {error ? <p>{error.message}</p> : null}
        {!isLoading ? (
          newItems.map((timezone) => {
            return (
              <div key={timezone}>
                <p>Timezone: {timezone}</p>
                <ClockTimezone timezone={timezone}/>
              </div>
            );
          })
        ) : (
          // If there is a delay in data, let's let the user know it's loading
          <h3>Loading...</h3>
        )}
      </React.Fragment>
    );
  }
}
